mapper.select:
SELECT REVIEW_NO, REVIEW_TITLE, REVIEW_CONTENT, REVIEWER_USER_ID, REVIEW_RATING, REVIEW_VIEWS
FROM REVIEW_BOARD
WHERE REVIEW_NO = #{reviewNo}

SELECT PHOTO_PATH || '/' || PHOTO_NAME AS photoUrl
FROM REVIEW_PHOTO_BOARD
WHERE REVIEW_NO = #{reviewNo}

mapper.update:
UPDATE REVIEW_BOARD
SET REVIEW_VIEWS = REVIEW_VIEWS + 1
WHERE REVIEW_NO = #{reviewNo}
